package fakeamazon.bookstore.demo;

import com.fasterxml.jackson.databind.ObjectMapper;
import fakeamazon.bookstore.demo.model.Book;
import fakeamazon.bookstore.demo.repository.BookRepository;
import org.junit.jupiter.api.Test;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.boot.test.autoconfigure.web.servlet.AutoConfigureMockMvc;
import org.springframework.boot.test.context.SpringBootTest;
import org.springframework.http.MediaType;
import org.springframework.test.web.servlet.MockMvc;
import org.springframework.test.web.servlet.request.MockMvcRequestBuilders;

import static org.hamcrest.Matchers.containsString;
import static org.springframework.test.web.servlet.request.MockMvcRequestBuilders.get;
import static org.springframework.test.web.servlet.result.MockMvcResultHandlers.print;
import static org.springframework.test.web.servlet.result.MockMvcResultMatchers.content;
import static org.springframework.test.web.servlet.result.MockMvcResultMatchers.status;


@SpringBootTest
@AutoConfigureMockMvc
class BookstoreBackendApplicationTests {

	@Autowired
	private MockMvc mockMvc;

	public static String asJsonString(final Object obj) {
		try {
			return new ObjectMapper().writeValueAsString(obj);
		} catch (Exception e) {
			throw new RuntimeException(e);
		}
	}

	@Autowired
	private BookRepository bookRepository;

	@Test
	void testGetAllBooks() throws Exception {
		bookRepository.save(new Book("book1", "desc"));

		this.mockMvc.perform(get("/getAllBooks")).andDo(print()).andExpect(status().isOk())
				.andExpect(content().string(containsString("\"name\":\"book1\"")))
				.andExpect(content().string(containsString("\"description\":\"desc\"")))
				.andExpect(content().string(containsString("\"id\":1")));

		bookRepository.save(new Book("book2", "desc2"));

		this.mockMvc.perform(get("/getAllBooks")).andDo(print()).andExpect(status().isOk())
				.andExpect(content().string(containsString("\"name\":\"book1\"")))
				.andExpect(content().string(containsString("\"description\":\"desc\"")))
				.andExpect(content().string(containsString("\"id\":1")))
				.andExpect(content().string(containsString("\"name\":\"book2\"")))
				.andExpect(content().string(containsString("\"description\":\"desc2\"")));

	}

	@Test
	void testUploadBook() throws Exception {

		Book bookToAdd = new Book();
		bookToAdd.setName("Refactoring");
		bookToAdd.setPicture("data:image/jpeg;base64,");
		bookToAdd.setDescription("A book of refactoring concepts.");
		bookToAdd.setQuantity(1);
		bookToAdd.setPublisher("Fowler");
		bookToAdd.setIsbn("ST193821");

		System.out.println(asJsonString(bookToAdd));

		this.mockMvc.perform(MockMvcRequestBuilders.post(
				"http://localhost:8080/owneractions/upload")
						.content(asJsonString(bookToAdd))
						.contentType(MediaType.APPLICATION_JSON_VALUE)
				).andExpect(status().isCreated());

		this.mockMvc.perform(MockMvcRequestBuilders.get(
				"http://localhost:8080/getAllBooks"
		))
				.andExpect(status().isOk())
				.andExpect(content().string(containsString(bookToAdd.getName())))
				.andExpect(content().string(containsString(bookToAdd.getPicture())))
				.andExpect(content().string(containsString(bookToAdd.getDescription())))
				.andExpect(content().string(containsString(String.valueOf(bookToAdd.getQuantity()))))
				.andExpect(content().string(containsString(bookToAdd.getPublisher())))
				.andExpect(content().string(containsString(bookToAdd.getIsbn())));
	}

	@Test
	void testEditBook() throws Exception{
		Book bookToAdd = new Book();
		bookToAdd.setName("Refactoring2");
		bookToAdd.setPicture("data:image/jpeg;base64,");
		bookToAdd.setDescription("A book of refactoring concepts.");
		bookToAdd.setQuantity(1);
		bookToAdd.setPublisher("Fowler");
		bookToAdd.setIsbn("ST1938212");
		bookRepository.save(bookToAdd);
		Book oldBook = bookRepository.findByIsbn("ST1938212");

		Book newBook = new Book();
		newBook.setId(oldBook.getId());
		newBook.setName("Refactoring3");
		newBook.setPicture("data:image/jpeg;base64,");
		newBook.setDescription("A book of refactoring concepts.");
		newBook.setQuantity(1);
		newBook.setPublisher("Fowler");
		newBook.setIsbn("ST1938212");

		this.mockMvc.perform(MockMvcRequestBuilders.patch("http://localhost:8080/owneractions/edit?id=" + oldBook.getId()).content(asJsonString(newBook)).contentType(MediaType.APPLICATION_JSON_VALUE))
				.andExpect(status().isOk()).andExpect(content().string(containsString("Refactoring3")));

	}
}
